    <script type="text/javascript">
        $(function() {

            // Inicializo tooltips de ayuda
            tippy('.tippy', {
                arrow: true,
                animation: 'fade',
                delay: [0,0]
            });

            // Inicializo el ordenamiento materias con drag and drop
            $('.curso-sortable').sortable({
                animation: 150,
                handle: '.sort-handler',
                forceFallback: true,
                onSort: function(e) {
                    const comisiones = $('.materia-bloque').map(function(){ return $(this).data("id"); }).get();
                    $.ajax({
                        type: "POST",
                        url: "https://miel.unlam.edu.ar/principal/event/ordenarComisionesAjax/" ,
                        data: { "idComisiones": comisiones },
                        dataType: "text"
                    });
                },
            });

            // Inicializo los popups con informacion y tutores de los cursos
            $('.popup-curso').magnificPopup({
                type: 'ajax',
                autoFocusLast: false
            });

            // Reduce la fuente del nombre de la materia si ocupa dos renglones
            $(".materia-titulo").each(function(i) {
                if ($(this).height() > 30)
                    $(this).removeClass("w3-slarge").css("line-height", "1.35");
            });

            // Marcar notificacion de inhabilitacion como leida
            $(".botonLeerNotificacion").click(function() {
                if (verificarSesion()) {
                    var panel = $(this).closest(".w3-panel");
                    $.ajax({
                        type: "POST",
                        url: "https://miel.unlam.edu.ar/principal/event/marcarLeidaAjax/",
                        data: { idNotificacion: $(this).attr("data-idNotificacion") },
                        cache: false,
                        success: function(data) {
                            if (data == "1") {
                                panel.hide();
                            }
                        }
                    });
                } else {
                    alert("Su sesi\xf3n ha finalizado. Por favor, ingrese nuevamente.");
                }
            });

            // Boton para cambiar al modo de organizacion de cursos
            $(".btnOrganizar").click(function() {
                $(".botoneraOrganizar").toggleClass("w3-hide");
                $(".botoneraDetalle").toggleClass("w3-hide");
            });

            // Muestro el boton de mostrar ocultas si hay materias ocultas
            if ($(".materia-bloque-oculta").length > 0) {
                $(".btnMostrarOcultas").removeClass("w3-hide");
            }

            // Boton para mostrar las materias ocultas
            $(".btnMostrarOcultas").click(function() {
                $(".materia-bloque-oculta").removeClass("w3-hide");
                $(".btnMostrarOcultas").addClass("w3-hide");
                $(".btnOcultarOcultas").removeClass("w3-hide");
            });

            // Boton para ocultar las materias ocultas
            $(".btnOcultarOcultas").click(function() {
                $(".materia-bloque-oculta").addClass("w3-hide");
                $(".btnOcultarOcultas").addClass("w3-hide");
                $(".btnMostrarOcultas").removeClass("w3-hide");
            });

            // Boton para ocultar/desocultar una materia
            $(".btnOcultarComision").click(function() {
                const botonComision = $(this);
                const btnMostrarOcultas = $('.btnMostrarOcultas');
                const btnOcultarOcultas = $('.btnOcultarOcultas');
                const bloqueComision = $(this).closest('.materia-bloque');
                const ocultar = bloqueComision.hasClass("materia-bloque-oculta") ? 0 : 1;
                $.ajax({
                    type: "POST",
                    url: "https://miel.unlam.edu.ar/principal/event/ocultarComisionAjax/" ,
                    data: { idComision: bloqueComision.data("id"), ocultar },
                    dataType: "text",
                    success: function() {
                        if (ocultar === 1) {
                            botonComision[0]._tippy.setContent("Desocultar materia");
                            bloqueComision.addClass("materia-bloque-oculta");
                            botonComision.find("i").text("replay");
                            if (btnMostrarOcultas.hasClass("w3-hide")) {
                                if (btnOcultarOcultas.hasClass("w3-hide")) {
                                    btnMostrarOcultas.removeClass("w3-hide");
                                    bloqueComision.addClass("w3-hide");
                                }
                            } else {
                                bloqueComision.addClass("w3-hide");
                            }
                        } else {
                            botonComision[0]._tippy.setContent("Ocultar materia");
                            bloqueComision.removeClass("materia-bloque-oculta");
                            botonComision.find("i").text("close");
                            if ($(".materia-bloque-oculta").length === 0) {
                                btnOcultarOcultas.addClass("w3-hide");
                            }
                        }
                    }
                });
            });

        });
    </script>

